import Axios from "axios";
import { decode } from "jsonwebtoken";
import { nanoid } from "nanoid";

const baseUrl = process.env.BASE_URL ?? "https://api-staging.polygonid.com/v1";

export const refreshToken = async (token: string): Promise<string> => {
  try {
    const { data } = await Axios.post(
      `${baseUrl}/orgs/account-management/refresh-token`,
      {},
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    const { token: updatedToken } = data;
    return updatedToken;
  } catch (err) {
    throw {
      statusCode: 500,
      message: "Cannot update Refresh Token!",
    };
  }
};

export const registerOrg = async (
  email: string,
  password: string
): Promise<void> => {
  try {
    const { data } = await Axios.post(`${baseUrl}/orgs/account-management`, {
      email: email,
      password: password,
    });
    console.log("Org registered:");
    console.dir(data);
  } catch (err: Error | any) {
    console.error(err?.response?.data);
    throw {
      statusCode: 500,
      message: "Could not register organization!",
    };
  }
};

export const signInOrg = async (
  email: string,
  password: string
): Promise<{ isVerified: boolean; token: string | null }> => {
  try {
    const { data, headers } = await Axios.post(`${baseUrl}/orgs/sign-in`, {
      email: email,
      password: password,
    });
    const { token } = data;
    const payload = decode(token, { json: true });
    const isVerified = payload?.account?.verified;
    return {
      isVerified: isVerified,
      token: token,
    };
  } catch (err: Error | any) {
    console.error(err?.response?.data);
    throw {
      statusCode: 500,
      message: "Could not sign-in to organization!",
    };
  }
};

export const activateOrg = async (token: string): Promise<string> => {
  try {
    const { data } = await Axios.post(
      `${baseUrl}/orgs/account-management/activate`,
      {},
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    const { token: updatedToken } = data;
    return updatedToken;
  } catch (err: Error | any) {
    console.error(err?.response?.data);
    throw {
      statusCode: 500,
      message: "Could not activate organization!",
    };
  }
};

export const completeIssuerFlow = async (oldToken: string) => {
  try {
    const activatedToken = await activateOrg(oldToken);
    const slug = nanoid(10);
    const { data } = await Axios.post(
      `${baseUrl}/issuers`,
      {
        displayName: `Collab.Land - ${slug}`,
        logo: "data:image/png;base64,",
      },
      {
        headers: {
          Authorization: `Bearer ${activatedToken}`,
        },
      }
    );
    console.log("Issuer Created:");
    console.dir(data);
    return await refreshToken(activatedToken);
  } catch (err: Error | any) {
    console.error(err?.response?.data);
    throw {
      statusCode: 500,
      message: "Could not create issuer for organization!",
    };
  }
};

export const createSchema = async (
  schema: string,
  mandatoryExpiration: boolean,
  attributes: any,
  token: string,
  issuerId: string
) => {
  try {
    const { data } = await Axios.post(
      `${baseUrl}/issuers/${issuerId}/schemas`,
      {
        schema: schema,
        mandatoryExpiration: mandatoryExpiration,
        attributes: attributes,
      },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    console.log("Schema created:");
    console.dir(data);
    return data?.id;
  } catch (err) {
    console.error(err);
    throw {
      statusCode: 500,
      message: "Could not create schema!",
    };
  }
};

export const getIssuerID = (token: string) => {
  const payload = decode(token, { json: true });
  const issuerId = payload?.account?.organization;
  return issuerId;
};

export const getAllSchemas = async (token: string, issuerId: string) => {
  const { data } = await Axios.get(`${baseUrl}/issuers/${issuerId}/schemas`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
  return data;
};

export const createClaimOffer = async (
  issuerId: string,
  schemaId: string,
  attributes: any,
  token: string
) => {
  try {
    const { data } = await Axios.post(
      `${baseUrl}/issuers/${issuerId}/schemas/${schemaId}/offers`,
      {
        attributes: attributes,
      },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    console.log("Claim created:");
    console.dir(data);
    const claimId = data?.id;
    const { data: qrData } = await Axios.post(
      `${baseUrl}/offers-qrcode/${claimId}`
    );
    console.log("QR code data:");
    console.dir(qrData?.qrcode);
    const sessionId = qrData?.sessionID;
    const { data: qrImage } = await Axios.get(
      `${baseUrl}/offers-qrcode/${claimId}/download?sessionID=${sessionId}`,
      {
        responseType: "arraybuffer",
      }
    );
    const imageBuffer = Buffer.from(qrImage);
    return {
      qrCode: qrData?.qrcode,
      qrBuffer: imageBuffer,
    };
  } catch (err) {
    console.error(err);
    throw {
      statusCode: 500,
      message: "Cannot able to create claim!",
    };
  }
};
